import collections
class Solution:
    def maxAreaOfIsland(self, grid: list[list[int]]) -> int:
        area = 0
        
        for x in range(len(grid)):
            for y in range(len(grid[x])):
                if grid[x][y] == 1:
                    area = max(area, self.calculateArea(grid, x, y))

        return area
    
    def calculateArea(self, grid: list[list[int]], x, y) -> int:
        directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
        q = collections.deque()
        q.append((x, y))

        area = 0
        biggest = 0
        while len(q) > 0:
            print(len(q))
            biggest = max(biggest, len(q))
            px, py = q.popleft()
            area += 1

            for dx, dy in directions:
                x = px + dx
                y = py + dy
                if 0 <= x < len(grid) and 0 <= y < len(grid[x]) and grid[x][y] == 1:
                    q.append((x, y))
                    grid[x][y] = 0

        print(biggest)
        return area

