from typing import List
import collections
class Solution:
    def shortestBridge(self, grid: List[List[int]]) -> int:
        n = len(grid)
        directions = [-1, 0, 1, 0, -1]
        points = self.findIsland(grid)
        q = collections.deque(points)
        res = 0

        while len(q) > 0:
            res += 1            
            level = len(q)
            for _ in range(level):
                x, y = q.popleft()
                for k in range(4):
                    dx = x + directions[k]
                    dy = y + directions[k + 1]
                    if 0 <= dx < n and 0 <= dy < n:
                        if grid[dx][dy] == 0:
                            q.append((dx, dy))
                            grid[dx][dy] = 2
                        elif grid[dx][dy] == 1:
                            return res
            
        return -1

    def findIsland(self, grid: List[List[int]]) -> list:
        n = len(grid)
        directions = [-1, 0, 1, 0, -1]
        points = []
        for i in range(n):
            for j in range(n):
                if grid[i][j] == 1:
                    stack = [(i, j)]
                    visited = set()

                    while len(stack) > 0:
                        x, y = stack.pop()
                        visited.add((x, y))
                        grid[x][y] = 2
                        
                        for k in range(4):
                            dx = x + directions[k]
                            dy = y + directions[k + 1]
                            if 0 <= dx < n and 0 <= dy < n and (dx, dy) not in visited:
                                if grid[dx][dy] == 0:
                                    points.append((dx, dy))
                                elif grid[dx][dy] == 1:
                                    stack.append((dx, dy))
                                
                    return points

